#ifdef GL_ES
#define LOWP lowp
precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
//https://github.com/gl-transitions/gl-transitions/blob/master/transitions/BookFlip.glsl
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }


vec2 skewRight(vec2 p) {
    float skewX = (p.x - dm_progress)/(0.5 - dm_progress) * 0.5;
    float skewY =  (p.y - 0.5)/(0.5 + dm_progress * (p.x - 0.5) / 0.5)* 0.5  + 0.5;
    return vec2(skewX, skewY);
}

vec2 skewLeft(vec2 p) {
    float skewX = (p.x - 0.5)/(dm_progress - 0.5) * 0.5 + 0.5;
    float skewY = (p.y - 0.5) / (0.5 + (1.0 - dm_progress ) * (0.5 - p.x) / 0.5) * 0.5  + 0.5;
    return vec2(skewX, skewY);
}

vec4 addShade() {
    float shadeVal  =  max(0.7, abs(dm_progress - 0.5) * 2.0);
    return vec4(vec3(shadeVal ), 1.0);
}

vec4 transition (vec2 p) {
    float pr = step(1.0 - dm_progress, p.x);

    if (p.x < 0.5) {
        return mix(getFromColor(p), getToColor(skewLeft(p)) * addShade(), pr);
    } else {
        return mix(getFromColor(skewRight(p)) * addShade(), getToColor(p),   pr);
    }
}


void main(void){
    gl_FragColor=transition(vTexCoord);

}

#ifdef GL_ES
#define LOWP lowp
precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }


uniform float shadow_height; // = 0.075
uniform float bounces; // = 3.0
uniform float shacolor_r;// =0.0
uniform float shacolor_g;// =0.0
uniform float shacolor_b;// =0.0
uniform float shacolor_a;// =0.6
const float PI = 3.14159265358;

vec4 transition (vec2 uv) {
    float time = dm_progress;
    float stime = sin(time * PI / 2.);
    float phase = time * PI * bounces;
    float y = (abs(cos(phase))) * (1.0 - stime);
    float d = uv.y - y;
    vec4 shadow_colour= vec4(shacolor_r,shacolor_g,shacolor_b,shacolor_a);
    return mix(
    mix(
    getToColor(uv),
    shadow_colour,
    step(d, shadow_height) * (1. - mix(
    ((d / shadow_height) * shadow_colour.a) + (1.0 - shadow_colour.a),
    1.0,
    smoothstep(0.95, 1., dm_progress) // fade-out the shadow at the end
    ))
    ),
    getFromColor(vec2(uv.x, uv.y + (1.0 - y))),
    step(d, 0.0)
    );
}




void main(void){
    gl_FragColor=transition(vTexCoord);

}

#ifdef GL_ES
#define LOWP lowp
precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }

vec2 bottom_left = vec2(0.0, 1.0);
vec2 bottom_right = vec2(1.0, 1.0);
vec2 top_left = vec2(0.0, 0.0);
vec2 top_right = vec2(1.0, 0.0);
vec2 center = vec2(0.5, 0.5);

float check(vec2 p1, vec2 p2, vec2 p3)
{
    return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
}

bool PointInTriangle (vec2 pt, vec2 p1, vec2 p2, vec2 p3)
{
    bool b1, b2, b3;
    b1 = check(pt, p1, p2) < 0.0;
    b2 = check(pt, p2, p3) < 0.0;
    b3 = check(pt, p3, p1) < 0.0;
    return ((b1 == b2) && (b2 == b3));
}

bool in_left_triangle(vec2 p){
    vec2 vertex1, vertex2, vertex3;
    vertex1 = vec2(dm_progress, 0.5);
    vertex2 = vec2(0.0, 0.5-dm_progress);
    vertex3 = vec2(0.0, 0.5+dm_progress);
    if (PointInTriangle(p, vertex1, vertex2, vertex3))
    {
        return true;
    }
    return false;
}

bool in_right_triangle(vec2 p){
    vec2 vertex1, vertex2, vertex3;
    vertex1 = vec2(1.0-dm_progress, 0.5);
    vertex2 = vec2(1.0, 0.5-dm_progress);
    vertex3 = vec2(1.0, 0.5+dm_progress);
    if (PointInTriangle(p, vertex1, vertex2, vertex3))
    {
        return true;
    }
    return false;
}

float blur_edge(vec2 bot1, vec2 bot2, vec2 top, vec2 testPt)
{
    vec2 lineDir = bot1 - top;
    vec2 perpDir = vec2(lineDir.y, -lineDir.x);
    vec2 dirToPt1 = bot1 - testPt;
    float dist1 = abs(dot(normalize(perpDir), dirToPt1));

    lineDir = bot2 - top;
    perpDir = vec2(lineDir.y, -lineDir.x);
    dirToPt1 = bot2 - testPt;
    float min_dist = min(abs(dot(normalize(perpDir), dirToPt1)), dist1);

    if (min_dist < 0.005) {
        return min_dist / 0.005;
    }
    else  {
        return 1.0;
    };
}


vec4 transition (vec2 uv) {
    if (in_left_triangle(uv))
    {
        if (dm_progress < 0.1)
        {
            return getFromColor(uv);
        }
        if (uv.x < 0.5)
        {
            vec2 vertex1 = vec2(dm_progress, 0.5);
            vec2 vertex2 = vec2(0.0, 0.5-dm_progress);
            vec2 vertex3 = vec2(0.0, 0.5+dm_progress);
            return mix(
            getFromColor(uv),
            getToColor(uv),
            blur_edge(vertex2, vertex3, vertex1, uv)
            );
        }
        else
        {
            if (dm_progress > 0.0)
            {
                return getToColor(uv);
            }
            else
            {
                return getFromColor(uv);
            }
        }
    }
    else if (in_right_triangle(uv))
    {
        if (uv.x >= 0.5)
        {
            vec2 vertex1 = vec2(1.0-dm_progress, 0.5);
            vec2 vertex2 = vec2(1.0, 0.5-dm_progress);
            vec2 vertex3 = vec2(1.0, 0.5+dm_progress);
            return mix(
            getFromColor(uv),
            getToColor(uv),
            blur_edge(vertex2, vertex3, vertex1, uv)
            );
        }
        else
        {
            return getFromColor(uv);
        }
    }
    else {
        return getFromColor(uv);
    }
}

void main(void) {
    gl_FragColor =transition(vTexCoord);

}

