#ifdef GL_ES
#define LOWP lowp
precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;

uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }

uniform float amplitude; // = 1.0
uniform float waves; // = 30.0
uniform float colorSeparation; // = 0.3
float PI = 3.14159265358979323846264;
float compute(vec2 p, float dm_progress, vec2 center) {
    vec2 o = p*sin(dm_progress * amplitude)-center;
    // horizontal vector
    vec2 h = vec2(1., 0.);
    // butterfly polar function (don't ask me why this one :))
    float theta = acos(dot(o, h)) * waves;
    return (exp(cos(theta)) - 2.*cos(4.*theta) + pow(sin((2.*theta - PI) / 24.), 5.)) / 10.;
}
vec4 transition(vec2 uv) {
    vec2 p = uv.xy / vec2(1.0).xy;
    float inv = 1. - dm_progress;
    vec2 dir = p - vec2(.5);
    float dist = length(dir);
    float disp = compute(p, dm_progress, vec2(0.5, 0.5)) ;
    vec4 texTo = getToColor(p + inv*disp);
    vec4 texFrom = vec4(
    getFromColor(p + dm_progress*disp*(1.0 - colorSeparation)).r,
    getFromColor(p + dm_progress*disp).g,
    getFromColor(p + dm_progress*disp*(1.0 + colorSeparation)).b,
    1.0);
    return texTo*dm_progress + texFrom*inv;
}




void main(void){
    gl_FragColor=transition(vTexCoord);

}

#ifdef GL_ES
#define LOWP lowp
precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }


uniform float smoothness; // = 0.3
uniform float opening; // = 1.0

const vec2 center = vec2(0.5, 0.5);
const float SQRT_2 = 1.414213562373;

vec4 transition (vec2 uv) {
    float x = opening==1.0 ? dm_progress : 1.-dm_progress;
    float m = smoothstep(-smoothness, 0.0, SQRT_2*distance(center, uv) - x*(1.+smoothness));
    return mix(getFromColor(uv), getToColor(uv), opening==1.0 ? 1.-m : m);
}



void main(void){
    gl_FragColor=transition(vTexCoord);

}

