vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }


uniform float uLineWidth;// = 0.1
uniform float uSpreadClr_x;// = 1.0
uniform float uSpreadClr_y;// = 0.0
uniform float uSpreadClr_z;// = 0.0
uniform float uHotClr_x;// = 0.9
uniform float uHotClr_y;// = 0.9
uniform float uHotClr_z;// = 0.2
uniform float uPow;// = 5.0
uniform float uIntensity;// = 1.0

vec2 hash(vec2 p)  // replace this by something better
{
    p = vec2(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)));
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}

float noise(in vec2 p) {
    const float K1 = 0.366025404;  // (sqrt(3)-1)/2;
    const float K2 = 0.211324865;  // (3-sqrt(3))/6;

    vec2 i = floor(p + (p.x + p.y) * K1);
    vec2 a = p - i + (i.x + i.y) * K2;
    float m = step(a.y, a.x);
    vec2 o = vec2(m, 1.0 - m);
    vec2 b = a - o + K2;
    vec2 c = a - 1.0 + 2.0 * K2;
    vec3 h = max(0.5 - vec3(dot(a, a), dot(b, b), dot(c, c)), 0.0);
    vec3 n = h * h * h * h * vec3(dot(a, hash(i + 0.0)), dot(b, hash(i + o)), dot(c, hash(i + 1.0)));
    return dot(n, vec3(70.0));
}

vec4 transition(vec2 uv) {
    vec4 from = getFromColor(uv);
    vec4 to = getToColor(uv);
    vec4 outColor;
    float burn;
    burn = 0.5 + 0.5 * (0.299 * from.r + 0.587 * from.g + 0.114 * from.b);
    vec3 uSpreadClr=vec3(uSpreadClr_x,uSpreadClr_y,uSpreadClr_z);
    vec3 uHotClr=vec3(uHotClr_x,uHotClr_y,uHotClr_z);
    float show = burn - dm_progress;
    if (show < 0.001) {
        outColor = to;
    } else {
        float factor = 1.0 - smoothstep(0.0, uLineWidth, show);
        vec3 burnColor = mix(uSpreadClr, uHotClr, factor);
        burnColor = pow(burnColor, vec3(uPow)) * uIntensity;
        vec3 finalRGB = mix(from.rgb, burnColor, factor * step(0.0001, dm_progress));
        outColor = vec4(finalRGB * from.a, from.a);
    }
    return outColor;
}


