varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }

uniform float reflection; // = 0.4
uniform float perspective; // = 0.4
uniform float depth; // = 3.0

const vec4 black = vec4(0.0, 0.0, 0.0, 1.0);
const vec2 boundMin = vec2(0.0, 0.0);
const vec2 boundMax = vec2(1.0, 1.0);

bool inBounds (vec2 p) {
    return all(lessThan(boundMin, p)) && all(lessThan(p, boundMax));
}

vec2 project (vec2 p) {
    return p * vec2(1.0, -1.2) + vec2(0.0, -0.02);
}

vec4 bgColor (vec2 p, vec2 pto) {
    vec4 c = black;
    pto = project(pto);
    if (inBounds(pto)) {
        c += mix(black, getToColor(pto), reflection * mix(1.0, 0.0, pto.y));
    }
    return c;
}


vec4 transition (vec2 p) {
    vec2 pfr = vec2(-1.), pto = vec2(-1.);
    float middleSlit = 2.0 * abs(p.x-0.5) - dm_progress;
    if (middleSlit > 0.0) {
        pfr = p + (p.x > 0.5 ? -1.0 : 1.0) * vec2(0.5*dm_progress, 0.0);
        float d = 1.0/(1.0+perspective*dm_progress*(1.0-middleSlit));
        pfr.y -= d/2.;
        pfr.y *= d;
        pfr.y += d/2.;
    }
    float size = mix(1.0, depth, 1.-dm_progress);
    pto = (p + vec2(-0.5, -0.5)) * vec2(size, size) + vec2(0.5, 0.5);
    if (inBounds(pfr)) {
        return getFromColor(pfr);
    }
    else if (inBounds(pto)) {
        return getToColor(pto);
    }
    else {
        return bgColor(p, pto);
    }
}



