vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }
uniform float dm_ratio;

// Definitions --------
#define DEG2RAD 0.03926990816987241548078304229099 // 1/180*PI


// Transition parameters --------

// In degrees
uniform float rotation; // = 6.0

// Multiplier
uniform float scale; // = 1.2


// The code proper --------

vec4 transition(vec2 uv) {
    // Massage parameters
    float phase = dm_progress < 0.5 ? dm_progress * 2.0 : (dm_progress - 0.5) * 2.0;
    float angleOffset = dm_progress < 0.5 ? mix(0.0, rotation * DEG2RAD, phase) : mix(-rotation * DEG2RAD, 0.0, phase);
    float newScale = dm_progress < 0.5 ? mix(1.0, scale, phase) : mix(scale, 1.0, phase);

    vec2 center = vec2(0, 0);

    // Calculate the source point
    vec2 assumedCenter = vec2(0.5, 0.5);
    vec2 p = (uv.xy - vec2(0.5, 0.5)) / newScale * vec2(dm_ratio, 1.0);

    // This can probably be optimized (with distance())
    float angle = atan(p.y, p.x) + angleOffset;
    float dist = distance(center, p);
    p.x = cos(angle) * dist / dm_ratio + 0.5;
    p.y = sin(angle) * dist + 0.5;
    vec4 c = dm_progress < 0.5 ? getFromColor(p) : getToColor(p);

    // Finally, apply the color
    return c + (dm_progress < 0.5 ? mix(0.0, 1.0, phase) : mix(1.0, 0.0, phase));
}



