        return getToColor(p);
    } else {
        float x = dm_progress;
        float dist = distance(center, p)- dm_progress*exp(snoise(vec2(p.x, 0.0)));
        float r = x - rand(vec2(p.x, 0.1));
        float m = dist <= r && luminance(getFromColor(p))<l_threshold ? 1.0 : (dm_progress*dm_progress*dm_progress);
        return mix(getFromColor(p), getToColor(p), m);
    }
}

