vec2 Rotate(vec2 v, float a) {
    mat2 rm = mat2(cos(a), -sin(a),
    sin(a), cos(a));
    return rm*v;
}
float CosInterpolation(float x) {
    return -cos(x*PI)/2.+.5;
}
vec4 transition(vec2 uv) {
    vec2 p = uv.xy / vec2(1.0).xy - .5;
    vec2 rp = p;
    float rpr = (dm_progress*2.-1.);
