    // thi no ok hon. nhung no van khon smooth
    vec2 uvMix = mix(uv,uv0,cos(dm_progress*PI*2.)/2.+0.5);
    vec4 color = mix(getFromColor(uvMix),getToColor(uvMix),cos((dm_progress-1.)*PI)/2.+0.5);
    return color;
}


