//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }


uniform float smoothness; // = 0.03

uniform float center_x; // = 0.5
uniform float center_y; // = 0.5
uniform float color_r; // =0.0
uniform float color_g; // =0.0
uniform float color_b; // =0.0


const float M_PI = 3.14159265358979323846;

float quadraticInOut(float t) {
    float p = 2.0 * t * t;
    return t < 0.5 ? p : -p + (4.0 * t) - 1.0;
}

float getGradient(float r, float dist) {
    float d = r - dist;
    return mix(
    smoothstep(-smoothness, 0.0, r - dist * (1.0 + smoothness)),
    -1.0 - step(0.005, d),
    step(-0.005, d) * step(d, 0.01)
    );
}

float getWave(vec2 p){
    vec2 center=vec2(center_x,center_y); // = vec2(0.5)
    vec2 _p = p - center; // offset from center
    float rads = atan(_p.y, _p.x);
    float degs = degrees(rads) + 180.0;
    vec2 range = vec2(0.0, M_PI * 30.0);
    vec2 domain = vec2(0.0, 360.0);
    float ratio = (M_PI * 30.0) / 360.0;
    degs = degs * ratio;
    float x = dm_progress;
    float magnitude = mix(0.02, 0.09, smoothstep(0.0, 1.0, x));
    float offset = mix(40.0, 30.0, smoothstep(0.0, 1.0, x));
    float ease_degs = quadraticInOut(sin(degs));
    float deg_wave_pos = (ease_degs * magnitude) * sin(x * offset);
    return x + deg_wave_pos;
}

vec4 transition(vec2 p) {
    vec2 center=vec2(center_x,center_y); // = vec2(0.5)
    float dist = distance(center, p);
    float m = getGradient(getWave(p), dist);
    vec4 cfrom = getFromColor(p);
    vec4 cto = getToColor(p);
    vec3 color=vec3(color_r,color_g,color_b);
    return mix(mix(cfrom, cto, m), mix(cfrom, vec4(color, 1.0), 0.75), step(m, -2.0));
}





void main(void){
    gl_FragColor=transition(vTexCoord);

}

