precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }
uniform float dm_ratio;


vec2 zoom(vec2 uv, float amount) {
    return 0.5 + ((uv - 0.5) * amount);
}


vec4 transition(vec2 uv) {
    vec2 ratio2 = vec2(1.0, 1.0 / dm_ratio);

    // TODO: some timing are hardcoded but should be one or many parameters
    // TODO: should also be able to configure how much circles
    // TODO: if() branching should be avoided when possible, prefer use of step() & other functions
    vec2 r = 2.0 * ((vec2(uv.xy) - 0.5) * ratio2);
    float pro = dm_progress / 0.8;
    float z = pro * 0.2;
    float t = 0.0;
    if (pro > 1.0) {
        z = 0.2 + (pro - 1.0) * 5.;
        t = clamp((dm_progress - 0.8) / 0.07, 0.0, 1.0);
    }
    if (length(r) < 0.5+z) {
        // uv = zoom(uv, 0.9 - 0.1 * pro);
    }
    else if (length(r) < 0.8+z*1.5) {
        uv = zoom(uv, 1.0 - 0.15 * pro);
        t = t * 0.5;
    }
    else if (length(r) < 1.2+z*2.5) {
        uv = zoom(uv, 1.0 - 0.2 * pro);
        t = t * 0.2;
    }
    else {
        uv = zoom(uv, 1.0 - 0.25 * pro);
    }
    return mix(getFromColor(uv), getToColor(uv), t);
}




void main(void){
    gl_FragColor=transition(vTexCoord);

}

#ifdef GL_ES
#define LOWP lowp
precision mediump float;
#else
#define LOWP
#endif
//"in" attributes from our vertex shader
varying LOWP vec4 vColor;
varying vec2 vTexCoord;
//our different texture units

precision highp float;
uniform sampler2D dm_from;
uniform sampler2D dm_to;
uniform float dm_progress;
vec4 getFromColor(vec2 uv){ return texture2D(dm_from, uv); }
vec4 getToColor(vec2 uv){ return texture2D(dm_to, uv); }


uniform float zoom_quickness; // = 0.8

vec2 zoom(vec2 uv, float amount) {
    if(amount<0.5)
    return 0.5 + ((uv - 0.5) * (1.0-amount));
    else
    return 0.5 + ((uv - 0.5) * (amount));

}

vec4 transition (vec2 uv) {
    float nQuick = clamp(zoom_quickness,0.0,0.5);
    if(dm_progress<0.5){
        vec4 c= mix(
        getFromColor(zoom(uv, smoothstep(0.0, nQuick, dm_progress))),
        getToColor(uv),
        step(0.5, dm_progress)
        );

        return c;
    }
    else{
        vec2 p=uv.xy/vec2(1.0).xy;
        vec4 d=getFromColor(p);
        vec4 e=getToColor(p);
        vec4 f= mix(d, e, step(1.0-p.x,(dm_progress-0.5)*2.0));

        return f;
    }
}

void main(void){
    gl_FragColor=transition(vTexCoord);

}

